document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#btn");const e=document.querySelector("#city-name"),t=document.querySelector("#form"),r=Swal.mixin({toast:!0,position:"top-end",showConfirmButton:!1,timer:3e3,timerProgressBar:!0,didOpen:e=>{e.addEventListener("mouseenter",Swal.stopTimer),e.addEventListener("mouseleave",Swal.resumeTimer)}});t.addEventListener("submit",t=>{""===e.value.trim()&&(t.preventDefault(),r.fire({icon:"error",title:"Please provide a city name."}))})});